using UnityEngine;using System.Collections;using System.Collections.Generic;public class PrefabPool  {    static private Dictionary<string,List<GameObject>> dict = new Dictionary<string,List<GameObject>>();    static public void restore(GameObject go, GameObject prefab)    {        if (dict.ContainsKey(prefab.name))        {            dict[prefab.name].Add(go);            go.SetActive(false);        }    }    static public GameObject instance(GameObject prefab)    {        if (!dict.ContainsKey(prefab.name))        {            dict[prefab.name] = new List<GameObject>();        }                if (dict[prefab.name].Count > 0)        {            GameObject go = dict[prefab.name][0];            dict[prefab.name].RemoveAt(0);
            go.SetActive(true);            return go;        }        return GameObject.Instantiate(prefab) as GameObject;    }    internal static void clean()    {        foreach(KeyValuePair<string, List<GameObject>>d in dict ){
            d.Value.Clear();
        }    }}